<?php		if(!empty($_POST))	{		$msg=array();				if(empty($_POST['nm']) || empty($_POST['email']) || empty($_POST['query']))		{			$msg[]="S'il vous plaît remplir toutes les exigences";		}								if(is_numeric($_POST['fnm']))		{			$msg[]="Le nom doit être en format chaîne...";		}				if(is_numeric($_POST['email']))			{			$msg[]="Le nom doit être en format approprié...";		}						if(!empty($msg))		{			echo '<b>Error:-</b><br>';						foreach($msg as $k)			{				echo '<li>'.$k;			}		}		else		{			$nm=$_POST['nm'];			$email=$_POST['email'];			$question=$_POST['query'];						$link=mysql_connect("localhost","root","")or die("Impossible de se connecter...");						mysql_select_db("shop",$link) or die("Impossible de se connecter à la base de données...");						$query="insert into contact(con_nm,con_email,con_query)			values('$nm','$email','$question')";						mysql_query($query,$link) or die("Vous ne pouvez pas exécuter la requête...");						header("location:contact.php");		}	}	else	{		header("location:index.php");	}?>