<?php		if(!empty($_POST))	{		$msg="";				if(empty($_POST['fnm']) || empty($_POST['unm']) || empty($_POST['gender']) || empty($_POST['pwd']) || empty($_POST['cpwd']) || empty($_POST['mail']) || empty($_POST['contact'])||empty($_POST['city']))		{			$msg.="<li>S'il vous plaît remplir toutes les exigences";		}				 if($_POST['pwd'] == NULL)    {        echo 'Vous devez entrer un mote de passe ';    }						 if($_POST['unm'] == NULL)    {        echo 'Vous devez entrer un username';    }			if($_POST['cpwd'] == NULL)    {        echo 'Vous devez confirmer votre mot de passe';    }			 if($_POST['mail'] == NULL)    {        echo 'Vous devez entrer une adresse email';    }			 if($_POST['pwd'] != $_POST['cpwd'])    {        echo 'Les deux mots de passe doivent être identiques';    }				if($_POST['pwd']!=$_POST['cpwd'])		{			$msg.="<li>Veuillez resaisir votre mot de passe.....";		}				if(!ereg("^[a-z0-9_]+[a-z0-9_.]*@[a-z0-9_-]+[a-z0-9_.-]*\.[a-z]{2,5}$",$_POST['mail']))		{			$msg.="<li>Veuillez entrer une adresse email valide...";		}						if(strlen($_POST['pwd'])>10)		{			$msg.="<li>S'il vous plaît Entrez votre mot de passe en format limité....";		}				if(is_numeric($_POST['fnm']))		{			$msg.="<li>Le nom doit être en format chaîne...";		}				if($msg!="")		{			header("location:register.php?error=".$msg);		}		else		{			$fnm=$_POST['fnm'];			$unm=$_POST['unm'];			$pwd=$_POST['pwd'];			$gender=$_POST['gender'];			$email=$_POST['mail'];			$contact=$_POST['contact'];			$city=$_POST['city'];											$link=mysql_connect("localhost","root","")or die("Impossible de se connecter...");						mysql_select_db("shop",$link) or die("Impossible de se connecter à la base de données...");						$query="insert into user(u_fnm,u_unm,u_pwd,u_gender,u_email,u_contact,u_city,u_con)			values('$fnm','$unm','$pwd','$gender','$email','$contact','$city',0)";						mysql_query($query,$link) or die("Vous ne pouvez pas exécuter la requête...");			header("location:register.php?ok=1");		}	}	else	{		header("location:RIM.php");	}?>